AND 
	chatTerm=#{chatTerm}


AND 
	chatContent=#{chatContent}

SELECT 
	COUNT (*) TOTCNT
FROM 
	CHAT_DATA
WHERE 
	1=1

AND 
	chatSeq=#{chatSeq}


AND 
	fromUserId=#{fromUserId}


AND 
	toUserId=#{toUserId}


AND 
	state=#{state}


AND 
	retry=#{retry}


AND 
	chatRead=#{chatRead}


AND 
	chatTerm=#{chatTerm}


AND 
	chatContent=#{chatContent}

mapper.update:
UPDATE 
	CHAT_DATA
SET 
    fromUserId=#{fromUserId}

	, toUserId=#{toUserId}


	, state=#{state}


	, chatTime=#{chatTime}


	, retry=#{retry}


	, chatRead=#{chatRead}


	, chatTerm=#{chatTerm}


	, chatContent=#{chatContent}



WHERE 
	chatSeq=#{chatSeq}
